/* Container holding the image and the text */
#carcontainer {
  position: relative;
  text-align: center;
  color: white;
}

.carstatusimage {}

.carimage {}

.carstatuslabel {
  fill: white;
  font-size: 1.5em;
}

.chargercablecharging {
  stroke-dasharray: 10;
  animation: dashdraw 4s linear infinite;
  stroke: green;
}
.chargercablechargingSlow {
  stroke-dasharray: 10;
  animation: dashdraw 10s linear infinite;
  stroke: green;
}
.chargercablechargingFast {
  stroke-dasharray: 10;
  animation: dashdraw 1s linear infinite;
  stroke: orange;
}
@keyframes dashdraw {to {stroke-dashoffset: 200;}}

.chargercableconnected {
  stroke: gray;
}
.chargercableerror {
  stroke: red;
}
.chargercabledisconnected {
  stroke: none;
}
.drivepath {
  stroke-dasharray: 100 200;
  stroke-dashoffset: 350;
  animation: dashdrawdrivepath 1s linear infinite;
  animation-direction: forward;
  stroke: rgb(215, 215, 215);
}
.drivepath2 {
  stroke: rgb(215, 215, 215);
}
.notdriving {
  stroke: none;
}
@keyframes dashdrawdrivepath {to {stroke-dashoffset: 50;}}

