options.onRender:
const vin = getTemplateSrv().replace(`$VIN`);
const vwsfriend_url = getTemplateSrv().replace(`$vwsfriend_url`);
const carimage = htmlNode.querySelector('#carimage');
const carlink = htmlNode.querySelector('#carlink');

const filteredSeriesImage = data.series.filter((series) => series.name == 'A');
jsonImage = null;
if(filteredSeriesImage.length > 0 && filteredSeriesImage[0].fields != null && filteredSeriesImage[0].fields.length > 0){
  jsonImage = filteredSeriesImage[0].fields[0].values.get(0);
  const vehicleImage = JSON.parse(jsonImage);
  carimage.src = "data:"+vehicleImage.type+";"+vehicleImage.encoding+","+vehicleImage.data
}
else {
  carimage.src = vwsfriend_url+'/status/vehicles/'+vin+'-status_or_car-badge.png';
}


carlink.href = vwsfriend_url+'/status/vehicle/'+vin+'/';

const warningsDiv = htmlNode.getElementById('warningsDiv');
const warningsList = htmlNode.getElementById('warningsList');
warningsDiv.style.display = 'none'
while (warningsList.firstChild) {
    warningsList.firstChild.remove()
}
const logsDiv = htmlNode.getElementById('logDiv');
const logsList = htmlNode.getElementById('logsList');
logsDiv.style.display = 'none'
while (logsList.firstChild) {
    logsList.firstChild.remove()
}

function addWarning(html) {
  warningsDiv.style.display = ''
  warning = document.createElement("li")
  warning.style.color = 'yellow'
  var template = document.createElement('template');
  html = html.trim();
  template.innerHTML = html;
  warning.append(template.content);
  warningsList.appendChild(warning)
}

function addLog(html) {
  logsDiv.style.display = ''
  log = document.createElement("li")
  var template = document.createElement('template');
  html = html.trim();
  template.innerHTML = html;
  log.append(template.content);
  logsList.appendChild(log)
}

function getMetric(seriesName, metricName) {
  const filteredSeries = data.series.filter((series) => series.refId == seriesName);
  if(filteredSeries.length > 0 && filteredSeries[0].fields != null
        && filteredSeries[0].fields.length > 0){
    const filteredFields = filteredSeries[0].fields.filter((fields) => fields.name == metricName)
    if(filteredFields.length > 0){
      return filteredFields[0].values.get(0)
    }
  }
  return null
}

const vwsfriend_url = getTemplateSrv().replace(`$vwsfriend_url`);
const vin = getTemplateSrv().replace(`$VIN`);
if(vwsfriend_url == null || vwsfriend_url== ''){
  addWarning("You have to set the URL for the VWsFriend user interface correctly: Go to the user interface on port 4000 and select \"Database\" > \”Connection Settings\"")
}

const carType = getMetric('vehicle_settings', 'carType')
const nickname = getMetric('vehicle_settings', 'nickname')
const primaryCapacity = getMetric('vehicle_settings', 'primary_capacity')
const primaryCapacityTotal = getMetric('vehicle_settings', 'primary_capacity_total')
const secondaryCapacity = getMetric('vehicle_settings', 'secondary_capacity')
const secondaryCapacityTotal = getMetric('vehicle_settings', 'secondary_capacity_total')
const timezone = getMetric('vehicle_settings', 'timezone')

if(carType == null){
  addWarning("The engine type of "+nickname+" could not be detected yet. This should happen soon when receiving data.")
}
else{
  switch (carType) {
    case 'HYBRID':
      if(primaryCapacity == null){
        addWarning("Some panels need the tank size of "+nickname+" to be set. Please set the size here: <a href=\""+vwsfriend_url+"/settings/vehicle/database/"+vin+"\" target=\”_blank\”>Vehicle Parameters</a>")
      }
      if(secondaryCapacity == null || secondaryCapacityTotal == null){
        addWarning("Some panels need the battery capacity of "+nickname+" to be set. Please set the size here: <a href=\""+vwsfriend_url+"/settings/vehicle/database/"+vin+"\" target=\”_blank\”>Vehicle Parameters</a>")
      }
      break;
    case 'ELECTRIC':
      if(primaryCapacity == null || primaryCapacityTotal == null){
        addWarning("Some panels need the battery capacity of "+nickname+" to be set. Please set the size here: <a href=\""+vwsfriend_url+"/settings/vehicle/database/"+vin+"\" target=\”_blank\”>Vehicle Parameters</a>")
      }
      break;
    default:
      if(primaryCapacity == null){
        addWarning("Some panels need the battery capacity of "+nickname+" to be set. Please set the size here: <a href=\""+vwsfriend_url+"/settings/vehicle/database/"+vin+"\" target=\”_blank\”>Vehicle Parameters</a>")
      }
      break;
  }
}
if(timezone == null){
  addWarning("Some panels need the timezone of "+nickname+" to be set. Please set the timezone here: <a href=\""+vwsfriend_url+"/settings/vehicle/database/"+vin+"\" target=\”_blank\”>Vehicle Parameters</a>")
}
const hasError = getMetric('error', 'has_error')
if(hasError){
  addWarning("There were more then two errors in the last 30 minutes when fetching data from the servers.<br/>It seems that there are technical issues.")
}

//Log
const filteredSeries = data.series.filter((series) => series.refId == 'log');
  if(filteredSeries.length > 0 && filteredSeries[0].fields != null
        && filteredSeries[0].fields.length > 0){
    const filteredFields = filteredSeries[0].fields.filter((fields) => (fields.name == 'logtime' || fields.name == 'log'))
    timeZone = htmlGraphics.props.timeZone;
    if(filteredFields.length == 2){
      time = filteredFields[0];
      logstring = filteredFields[1];
      var options = { dateStyle: 'short' };
      if(timeZone!='browser'){
        options.timeZone = timeZone;
      }
      var timeOptions = { timeStyle: 'short' };
      if(timeZone!='browser'){
        timeOptions.timeZone = timeZone;
      }
      var beforedate = new Intl.DateTimeFormat('default', options).format(new Date());
      for(let i = 0; i < time.values.length; i++) {
        var date = new Intl.DateTimeFormat('default', options).format(new Date(time.values.get(i)));
        if(beforedate != date){
          addLog('<b>'+date+'</b>');
        }
        beforedate = date;
        var timestr = new Intl.DateTimeFormat('default', timeOptions).format(new Date(time.values.get(i)));
        addLog(timestr+": "+logstring.values.get(i));
      }
    }
  }
